(ns adventofcode.2015.day01
  "https://adventofcode.com/2015/day/1")


(defn f1 [parens]
  (->> parens
    frequencies
    ((juxt
       #(get % \( 0)
       #(get % \) 0)))
    (apply -)))


(assert (= 0 (f1 "(())")))
(assert (= 0 (f1 "()()")))
(assert (= 3 (f1 "(((")))
(assert (= 3 (f1 "(()(()(")))
(assert (= 3 (f1 "))(((((")))
(assert (= -1 (f1 "())")))
(assert (= -1 (f1 "))(")))
(assert (= -3 (f1 ")))")))
(assert (= -3 (f1 ")())())")))


(defn f2 [parens]
  (let [f {\( inc
           \) dec}]
    (loop [todo parens
           idx  0
           res  0]
      (if (neg? res)
        idx
        (when-let [x (first todo)]
          (recur (rest todo) (inc idx) ((f x) res)))))))


(assert (= 1 (f2 ")")))
(assert (= 5 (f2 "()())")))


