;(def input ".#.#.#\n...##.\n#....#\n..#...\n#.#..#\n####..")

(def lights
  (->> input
    (str/split-lines)
    (mapv vec)))

(def MAX (count lights))

(def corner-ids
  (let [x (dec MAX)]
    #{[0 0] [0 x] [x 0] [x x]}))

(def stuck-lights
  (reduce
    #(assoc-in %1 %2 \#)
    lights
    corner-ids))

(def all-ids
  (for [x (range MAX)
        y (range MAX)]
    [x y]))

(def get-neighbor-ids
  (memoize
   (fn -get-neighbor-ids [[x y :as center]]
     (let [square (for [a (range (- x 1) (+ x 2))
                        b (range (- y 1) (+ y 2))
                        :when (and (< -1 a MAX) (< -1 b MAX))]
                    [a b])]
       (-> square (set) (disj center))))))


(def count-on
  (memoize
   (fn -count-on [xs]
     (-> xs frequencies (get \#)))))

(defn tick-one [starting current id]
  (let [v (get-in starting id)
        c (->> id
            (get-neighbor-ids)
            (map (partial get-in starting))
            (count-on))]
    ;; A light which is on stays on when 2 or 3 neighbors are on, and turns off otherwise.
    ;; A light which is off turns on if exactly 3 neighbors are on, and stays off otherwise.
    (case v
      \# (case c
           2 current
           3 current
             (assoc-in current id \.))
      \. (case c
           3 (assoc-in current id \#)
             current))))


(defn tick-all [ignore-id-fn lights]
  (->> all-ids
    (remove ignore-id-fn)
    (reduce (partial tick-one lights) lights)))


(defn -f [lights steps id-ignore-fn]
  (->> lights
    (iterate (partial tick-all id-ignore-fn))
    (take (inc steps))
    (last)
    (mapcat (partial filter #{\#}))
    (count)))

(defn f1 [lights steps] (-f lights steps (constantly false)))
(defn f2 [lights steps] (-f lights steps corner-ids))


#_
(assert (= 814 (f1 lights 100)))
#_
(let [two (f2 stuck-lights 100)]
  (assert (= two 924))
  (assert (not= 3330 two))
  (assert (not= 3331 two)))
