(defn merge-line [m s]
  (let [[left right] (str/split s #" -> ")
        [_ id weight] (re-matches #"([a-z]+) \((\d+)\)" left)
        top (some-> right (str/split #", "))]
    (assoc m id (cond-> {::id id ::weight (Integer/parseInt weight 10)}
                  top (assoc ::top top)))))

(defn parse [s] (->> s str/split-lines (reduce merge-line {})))

(def db (parse input))

(defn get-root [db]
  (loop [[x & todo] (->> db vals (filter ::top))
         top   #{}
         bottom #{}]
    (if (nil? x)
      (first (set/difference bottom top))
      (recur todo
        (into top (::top x))
        (conj bottom (::id x))))))

(assert (= "tknk" (get-root (parse sample))))
(assert (= "svugo" (get-root db)))

(defn get-weight [db id]
  (let [{::keys [weight top]} (db id)]
    (reduce + weight (map (partial get-weight db) top))))

(get-weight db "svugo")

;p2:
(let [db      (parse input)
      root-id (get-root db)
      get-weight (partial get-weight db)]
  (loop [req ##Inf
         id  root-id]
    (let [node    (db id)
          ids     (::top node)
          weights (map get-weight ids)
          outlier (-> weights frequencies set/map-invert (get 1))]
      (if-not outlier
        (reduce - req weights)
        (let [req (-> weights set (disj outlier) first)
              idx (->> weights
                    (map-indexed vector)
                    (filter #(-> % second (= outlier)))
                    (first)
                    (first))
              id  (nth ids idx)]
          (recur req id))))))

