(def CODE '{\A R \B P \C S
            \X R \Y P \Z S})

(def OUT '{[R R] D [P P] D [S S] D
           [S R] W [R S] L
           [R P] W [P R] L
           [P S] W [S P] L})

(def POINTS '{R 1 P 2 S 3 L 0 D 3 W 6})

(defn points [game]
  (+ (->> game (map OUT) (map POINTS) (reduce +))
     (->> game (map second) (map POINTS) (reduce +))))

(defn p1 [input]
  (-> input
    (str/split #"\n")
    (->>
      (map #(map CODE %))
      (map #(remove nil? %))
      points)))

(assert (= (p1 sample) 15))
(assert (= (p1 data) 15523))


(def SOLVE
  '{W {R P P S S R}
    D {R R P P S S}
    L {P R S P R S}})

(def CODE2 '{\A R \B P \C S
             \X L \Y D \Z W})


(defn p2 [input]
  (->> (str/split input #"\n")
    (map #(map CODE2 %))
    (map #(remove nil? %))
    (map (fn [[x y]] [x (get-in SOLVE [y x])]))
    (points)))

(assert (= 12 (p2 sample)))
(assert (= 15702 (p2 data)))