(defn split-boxes [width s]
  (let [[head tail] (split-at width s)]
    (if (seq tail)
      (into [head] (split-boxes width tail))
      [head])))

(defn parse [stepf ss]
  (let [[sqs steps] (str/split ss #"\n\n")
        qs     (str/split-lines sqs)
        nums   (peek qs)
        rows   (pop qs)
        width  (/ (inc (count nums)) (count (re-seq #"\d+" nums)))
        splitb (partial split-boxes width)
        letter (fn [chs] (some (set (str/upper-case u/letters)) chs))
        rows2  (->> rows
                 (map splitb)
                 (map #(map letter %)))
        qs2    (->> rows2
                 (apply map list)
                 (map #(remove nil? %))
                 (into [()]))]
    (->> steps
      str/split-lines
      (reduce stepf qs2)
      (map first)
      (str/join))))

(defn p1 [q step]
  (let [[n from to] (map u/to-int (re-seq #"\d+" step))
        f (fn [q]
            (let [x (-> q (nth from) first)]
              (-> q
                (update from rest)
                (update to conj x))))]
    (->> q (iterate f) (drop n) (first))))

(defn p2 [q step]
  (let [[n from to] (map u/to-int (re-seq #"\d+" step))]
    (let [xs (take n (nth q from))]
      (-> q
        (update from #(drop n %))
        (update to #(concat xs %))))))

(assert (= (parse p1 sample) "CMZ"))
(assert (= (parse p1 input) "VRWBSFZWM"))

(assert (= (parse p2 sample) "MCD"))
(assert (= (parse p2 input) "RBTWJWMCF"))