(ns adventofcode.y2022.day06)

(def sample "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")


(def input "")

(defn p1 [s] (->> s (partition-all 4 1) (take-while #(-> % set count (< 4))) count (+ 4)))
(defn p2 [s] (->> s (partition-all 14 1) (take-while #(-> % set count (< 14))) count (+ 14)))
(def p1 4)
(def p2 14)
(defn solve [width s] (->> s (partition-all width 1) (take-while #(-> % set count (< width))) count (+ width)))

(assert (= 11 (solve p1 sample)))
(assert (= 26 (solve p2 sample)))
(assert (= 1531 (solve p1 input)))
(assert (= 2518 (solve p2 input)))